import { Component } from '@angular/core';
import { FileService } from '../services/file-services';
import { DomSanitizer } from '@angular/platform-browser';


@Component({
  selector: 'app-image-viewer',
  templateUrl: './image-viewer.component.html',
  styleUrls: ['./image-viewer.component.css']
})
export class ImageViewerComponent {
  imageData: any;

  constructor(private fileService: FileService) {}

  ngOnInit() {
    const fileId = 1; // Örnek olarak fileId'yi belirtin.
    this.fileService.getFileById(fileId).subscribe((result: any) => {
      this.imageData = result;
      // Veriyi alındıktan sonra dönüşüm işlemini başlatın.
      this.convertDataToImage();
    });
  }

  convertDataToImage() {
    const binaryData = this.imageData.data; // Servisten gelen veri
    const blob = new Blob([binaryData], { type: 'image/jpeg' }); // Veriyi bir Blob'a dönüştürün
    const reader = new FileReader();

    reader.onload = () => {
      this.imageData = reader.result as string;
    };

    reader.readAsDataURL(blob);
  }
}


