import { Router } from '@angular/router';
import { Component, Input, OnInit, ViewChild } from '@angular/core';
import { CarDetailService } from "../services/car-detail.service"
import { CarDetailResponse } from '../response/car-detail-response';
import { ActivatedRoute } from '@angular/router';
import { ImageViewerComponent } from '../image-viewer/image-viewer.component';


@Component({
  selector: 'app-car-detail',
  templateUrl: './car-detail.component.html',
  styleUrls: ['./car-detail.component.css']
})

export class CarDetailComponent implements OnInit {
  @Input() item: CarDetailResponse | any;
  @ViewChild(ImageViewerComponent) imageViewerComponent: ImageViewerComponent;
 
  constructor(private carDetailService: CarDetailService, private activatedRoute : ActivatedRoute, private router : Router)
  { 
  }

  ngOnInit() {
    this.carDetailService.getItemsById(this.item.carId).subscribe({
      next: (data: CarDetailResponse) => {
        // next
      this.item = data;
       this.showFile(data.fileId);
      },
      error: (error) => {
        // error
        console.error('API isteği başarısız:', error);
      },
      complete: () => {
        // complete
      }
    }); 

  }

  showFile(fileId:number){
    if(fileId){ 
    this.imageViewerComponent.showFile(fileId);
  }}

  redirectToCarInfo(): void {
    // Butona tıklandığında "Car Info" component'e yönlendirme yapılıyor
    this.router.navigate(['/car-info/'+this.item.carId]); // "/car-info" route'unun tanımlı olması gerekiyor
  }
}
<div class="col">
  <div class="card">
    <div class="col-lg-12 mx-30">
      <div class="row">
        <div class="col-lg-4"><h5 class="card-title" style="margin-top:10px;">{{item?.brandName}}
            | {{item?.modelName}}</h5> </div>
        <div class="col-lg-4"></div>
        <div class="col-lg-2">
          <div class="col-lg-12"> <img
              src="https://dokay.com.tr/assets/img/fuel.svg" width="25"
              height="25" /></div>
          <div class="col-lg-12"><strong>{{item?.fuelTypeName}}</strong></div>
        </div>
        <div class="col-lg-2">
          <div class="col-lg-12"> <img
              src="https://dokay.com.tr/assets/img/gear.svg" width="25"
              height="25" /></div>
          <div class="col-lg-12"><strong>{{item?.gearTypeName}}</strong></div>
        </div>
      </div>
    </div>
    <div> 
      <app-image-viewer></app-image-viewer>
    </div>
    <div class="card-body">
      <h5 class="card-title"></h5>
      <p class="card-text"></p>
      <p class="card-text"><small class="text-muted">{{item?.createdDate}}</small></p>
    </div>
    <div class="card-footer">
      <div class="row">
        <div class="col-lg-3">
          <div class="row">
            <div class="col-lg-12"><img
                src="https://dokay.com.tr/assets/img/date.svg" width="25"
                height="25" alt></div>
            <div class="col-lg-12">Minimum<br>25 Yaş</div> </div>
        </div>
        <div class="col-lg-3">
          <div class="row">
            <div class="col-lg-12"><img
                src="https://dokay.com.tr/assets/img/id.svg" width="25"
                height="25" alt></div>
            <div class="col-lg-12">5 Yıllık<br>Ehliyet</div>
          </div> </div>
        <div class="col-lg-3">
          <div class="row">
            <div class="col-lg-12"><img
                src="https://dokay.com.tr/assets/img/credit_card.svg" width="25"
                height="25" alt></div>
            <div class="col-lg-12">1 Kredi<br>Kartı</div>
          </div>
        </div>
        <div class="col-lg-3">
          <div class="row">
            <div class="col-lg-12"><img
                src="https://dokay.com.tr/assets/img/credit_card_lock.svg"
                width="25" height="25" alt></div>
            <div class="col-lg-12">2500 TL<br>Teminat Tutarı</div>
            <div>
              <!-- Diğer içerikler burada olabilir -->
              <button class="btn btn-primary" [routerLink]="['/car-info', item?.carId]">Car Info</button>

            </div>
          </div>
        </div>
      </div>
    </div>

  </div>
</div>
