import { ImageViewerComponent } from './../image-viewer/image-viewer.component';
import { Component, Input, ViewChild, OnInit } from '@angular/core';
import { CarDetailService } from "../services/car-detail.service"
import { CarDetailResponse } from '../response/car-detail-response';

@Component({
  selector: 'app-car-search',
  templateUrl: './car-search.component.html',
  styleUrls: ['./car-search.component.css']
})
export class CarSearchComponent {
@ViewChild(ImageViewerComponent) imageViewerCompoent : ImageViewerComponent;
 item: any=null;
 carId : number=0;



 constructor(private carDetailService: CarDetailService)
 {

 }

 getByCarId(carId: number){
  this.carDetailService.getItemsById(carId).subscribe({
    next: (data: CarDetailResponse) => {
      // next
      this.item = data;
      this.imageViewerCompoent.showFile(this.item.fileId);
    },
    error: (error) => {
      // error
      console.error('API isteği başarısız:', error);
    },
    complete: () => {
      // complete
    }
  });
  console.log
}
}
<ng-container [style.display]="item ? 'block' : 'none'">
 <div style="text-align: center;" >

  <div class="row">
<div class="col-md-6">
  <app-image-viewer></app-image-viewer>
</div>
  <div class="col-md-6">
    <div class="table-cont">
    <table class="table table-bordered bg-white">
    <thead>
    <tr>
    <th colspan="2">
    <h4 class="tech">Teknik Özellikler</h4>
    </th>
    </tr>
    </thead>
    <tbody>
    <tr>
    <th scope="row">Marka</th>
    <td>{{ item.brandName }} </td>
    </tr>


    <tr>
    <th scope="row">Yakıt Tipi</th>
    <td> {{item.fuelTypeName}}</td>
    </tr>
    <tr>
    <th scope="row">Vites Tipi</th>
    <td> {{item.gearTypeName}} </td>
    </tr>

    </tbody>
    </table>
    </div>
    </div>
</div>
</div>
</ng-container>
    <div class="input-group mb-3" style="width: 300px; margin: 0 auto;">
        <input type="text" class="form-control" [(ngModel)]="carId"
            placeholder="Id">
        <div class="input-group-append">
            <button class="btn btn-primary" (click)="getByCarId(carId)">Ara</button>
        </div>
    </div>
