import { Component, Input } from '@angular/core';
import { CarDetailService } from "../services/car-detail.service"
import { CarDetailResponse } from '../car-detail/car-detail-response';

@Component({
  selector: 'app-car-search',
  templateUrl: './car-search.component.html',
  styleUrls: ['./car-search.component.css']
})
export class CarSearchComponent {

 item: any={};
 carId : number=0;
 items = ['Apple', 'Banana', 'Cherry', 'Date', 'Elderberry'];
  filteredItems: string[] = [];
  selectedItem!: string; // ! işareti ile tanımlandığını belirttik

  filterItems(filter: string): void {
    this.filteredItems = this.items.filter(t => t.toLowerCase().includes(filter.toLowerCase()));
  }
 constructor(private carDetailService: CarDetailService) 
 { 
  this.filteredItems = this.items;
 }
 getByCarId(carId: number){
  this.carDetailService.getItemsById(carId).subscribe({
    next: (data: CarDetailResponse) => {
      // next
      this.item = data;
      console.log(this.item?.carId);
    },
    error: (error) => {
      // error
      console.error('API isteği başarısız:', error);
    },
    complete: () => {
      // complete
    }
  }); 
  console.log
}
}
<div class="text-center">
    <h1>Araba Sorgu</h1> 
    <h6>Aranacak değer : {{carId}}</h6>
    <br>
    <div class="form-group">
        <label for="selectItem">Marka:</label> 
        <select class="form-control"> 
          <option *ngFor="let item of filteredItems" [value]="item">{{ item }}</option>
        </select>
      </div>
      <div class="form-group">
        <label for="selectItem">Model:</label> 
        <select class="form-control"> 
          <option *ngFor="let item of filteredItems" [value]="item">{{ item }}</option>
        </select>
      </div>
      <div class="form-group">
        <label for="selectItem">Vites Türü:</label> 
        <select class="form-control"> 
          <option *ngFor="let item of filteredItems" [value]="item">{{ item }}</option>
        </select>
      </div>
      <div class="form-group">
        <label for="selectItem">Yakıt Türü</label> 
        <select class="form-control"> 
          <option *ngFor="let item of filteredItems" [value]="item">{{ item }}</option>
        </select>
      </div>
    <div class="input-group mb-3" style="width: 300px; margin: 0 auto;">
        <input type="text" class="form-control" [(ngModel)]="carId"
            placeholder="Id">
        <div class="input-group-append">
            <button class="btn btn-primary" (click)="getByCarId(carId)">Ara</button>
        </div>
    </div>
    <div id="sonuc"></div>
</div>
