import { Component, Input, OnInit, ViewChild } from '@angular/core';
import { CarDetailService } from "../services/car-detail.service"
import { CarDetailResponse } from './car-detail-response';
import { ActivatedRoute } from '@angular/router';
import { ImageViewerComponent } from '../image-viewer/image-viewer.component';

@Component({
  selector: 'app-car-info',
  templateUrl: './car-info.component.html',
  styleUrls: ['./car-info.component.css']
})
export class CarInfoComponent implements OnInit {
  @ViewChild(ImageViewerComponent) imageViewerComponent: ImageViewerComponent;
  @Input() item: CarDetailResponse = new CarDetailResponse();
  carId : number;
  constructor(private carDetailService: CarDetailService, private activatedRoute : ActivatedRoute)
  {
    this.activatedRoute.params.subscribe(p => {
      this.carId = p["carId"]
    })
  }

  ngOnInit() {
    this.carDetailService.getItemsById(this.carId).subscribe({
      next: (data: CarDetailResponse) => {
        // next
        this.item = data;
        this.imageViewerComponent.showFile(this.item.fileId);
      },
      error: (error) => {
        // error
        console.error('API isteği başarısız:', error);
      },
      complete: () => {
        // complete
      }
    });
    console.log

  }

}
<div class = "app-image-viewer">
  {{item.fileId | json }}
<a>
  <app-image-viewer [fileId] = "item.fileId"></app-image-viewer>
</a>
</div>
<div style="text-align: center;">

  <div class="col-md-6">
    <div class="table-cont">
    <table class="table table-bordered bg-white">
    <thead>
    <tr>
    <th colspan="2">
    <h4 class="tech">Teknik Özellikler</h4>
    </th>
    </tr>
    </thead>
    <tbody>
    <tr>
    <th scope="row">Marka</th>
    <td>{{ item.brandName }} </td>
    </tr>


    <tr>
    <th scope="row">Yakıt Tipi</th>
    <td> {{item.fuelTypeName}}</td>
    </tr>
    <tr>
    <th scope="row">Vites Tipi</th>
    <td> {{item.gearTypeName}} </td>
    </tr>

    </tbody>
    </table>
    </div>
    </div>
</div>
